CustomField.formula:
if((b3o__Post_Job__c == true || b3o__Posted_on_Job_Boards__c > 0), if( text(b3o__Status__c) == "Open",

if((b3o__Available_Until__c > now() && not(isblank(b3o__Available_Until__c)))|| (isblank(b3o__Available_Until__c)), if((b3o__Available_From__c < now() && not(isblank(b3o__Available_From__c)) && not(isblank( b3o__Date_Posted__c )))|| (isblank(b3o__Available_From__c) && not(isblank( b3o__Date_Posted__c ))), "Live", "Not yet available (Missing 'Date Posted' or 'Available From')" ), "Not Available")

, if( text(b3o__Status__c) == "Closed", "Job Post is closed. This post is no longer live",  if( text(b3o__Status__c) == "On Hold", "Currently on hold, no applications are accepted", "Not Available"))), "Job is not posted yet")
